.page {
    page-break-before: always;
    margin: 1px;
    border: 1px solid #000;
    padding: 13px;
    height: 200%;
    line-height: 17px;
    page-break-inside: avoid; /* Prevent page break inside this element */



}
.fixed-height-element {
    height: 1000px;
}
